import YourUserGames from "./YourUserGames"
import { useNavigate } from "react-router-dom"
import { useState, useEffect } from 'react'


function Welcome({currentUser}) {

    const navigate = useNavigate()

    const handleClick = ()=>{



        fetch("/user_games", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(),
        })
        .then(response => 
            response.json())

        navigate("/trivia_game", {replace:true})
    }

    const mappedGames = currentUser.user_games.map((game) => (
        <YourUserGames
        key={game.id}
        gameId={game.id}
        name={game.name}
        score={game.score}
        ></YourUserGames>
      ))

    return(
        <div>
            <h1>Quiz Show</h1>
                <div>
                    <button className="pinkButton" onClick={handleClick}>
                        Start Trivia Game
                    </button>
                    <h2 className="score2">Your High Scores</h2>
                    {mappedGames} 
                </div>
        </div>
    )
}

export default Welcome
