import YourUserGames from "./YourUserGames"
import { useNavigate } from "react-router-dom"


function Welcome({currentUser}) {

    const navigate = useNavigate()

    useEffect(() => {
        const fetchData = async () => {
          try {
            const resp = await fetch("/user_games")
            const highScoreList = await resp.json()
            setHighScores(highScoreList)
          } catch (error) {
            alert(error)
          }
         }
        fetchData()  
      },[])



    const handleClick = ()=>{



        fetch("/user_games", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(),
        })
        .then(response => 
            response.json())

        navigate("/trivia_game", {replace:true})
    }

    return(
        <div>
            <h1>Quiz Show</h1>
                <div>
                    <button className="pinkButton" onClick={handleClick}>
                        Start Trivia Game
                    </button>
                    <h2 className="score2">Your High Scores</h2>
                    <YourUserGames/> 
                </div>
        </div>
    )
}

export default Welcome
